mapper.select:
select rownum num,m.id,m.name,m.phone,r.name as user_manager_role_name
,m.is_login from
t_u_manager
m,t_s_user_manager_role r where
m.user_manager_role_id=r.id and
m.is_delete!=1 and r.is_delete!=1

	and m.PHONE like
	concat('%',concat(#{keyword},'%'))


	and m.id=#{id}


	and createtime=#{createtime}


	and delete_user=#{delete_user}


	and updater=#{updater}


	and creator=#{creator}


	and deletetime=#{deletetime}


	and is_delete=#{is_delete}


	and updatetime=#{updatetime}


	and user_manager_role_id=#{user_manager_role_id}


	and phone=#{phone}


	and t_s_id=#{t_s_id}


	and pwd=#{pwd}


	and is_login=#{is_login}


	and name=#{name}
